mapper.select:
SELECT
,yonghu.yonghu_uuid_number as yonghuUuidNumber ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.new_money as newMoney ,yonghu.yonghu_email as yonghuEmail ,zhengce.zhengce_name as zhengceName ,zhengce.zhengce_uuid_number as zhengceUuidNumber ,zhengce.zhengce_photo as zhengcePhoto ,zhengce.zhengce_types as zhengceTypes ,zhengce.zhengce_content as zhengceContent ,zhengce.zhengce_delete as zhengceDelete
FROM zhengce_liuyan a left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id left JOIN zhengce zhengce ON a.zhengce_id = zhengce.id
and a.id in
#{item}
and ( a.zhengce_id = #{params.zhengceId} )
and ( a.yonghu_id = #{params.yonghuId} )
and a.zhengce_liuyan_text like CONCAT('%',#{params.zhengceLiuyanText},'%')
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.insert_time = #{params.insertTime}
and a.reply_text like CONCAT('%',#{params.replyText},'%')
and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart})
and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd})
and a.update_time = #{params.updateTime}
and a.yonghu_id IS NOT NULL
and yonghu.yonghu_uuid_number like CONCAT('%',#{params.yonghuUuidNumber},'%')
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.new_money >= #{params.newMoneyStart}
and yonghu.new_money <= #{params.newMoneyEnd}
and a.new_money = #{params.newMoney}
and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
and a.zhengce_id IS NOT NULL
and zhengce.zhengce_name like CONCAT('%',#{params.zhengceName},'%')
and zhengce.zhengce_uuid_number like CONCAT('%',#{params.zhengceUuidNumber},'%')
and zhengce.zhengce_types = #{params.zhengceTypes}
and zhengce.zhengce_content like CONCAT('%',#{params.zhengceContent},'%')
and zhengce.zhengce_delete >= #{params.zhengceDeleteStart}
and zhengce.zhengce_delete <= #{params.zhengceDeleteEnd}
and zhengce.zhengce_delete = #{params.zhengceDelete}
and UNIX_TIMESTAMP(zhengce.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(zhengce.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.insert_time = #{params.insertTime}
order by a.${params.sort} ${params.order}
